mapper.select:
select count(1)
from itrip_product_store


        and id = #{id}


        and productType = #{producttype}


        and productId = #{productid}


        and store = #{store}


        and creationDate = #{creationdate}


        and createdBy = #{createdby}


        and modifyDate = #{modifydate}


        and modifiedBy = #{modifiedby}
